mapper.select:
  	select distinct m.kor_title, m.rating_star, m.img_url, m.movie_id
	from bookmark_table as b, movie_table as m
  	where b.user_id = #{user_id} 
  	AND b.movie_id = m.movie_id;

select count(distinct m.movie_id)
from bookmark_table as b, movie_table as m
where b.user_id = #{user_id}
AND b.movie_id = m.movie_id;
